contracts/src/Quiz.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title QuizEscrow
 * @dev Simple escrow contract that holds funds for a quiz and distributes rewards
 * The actual quiz logic is managed off-chain
 */
contract QuizEscrow is Ownable {
    using SafeERC20 for IERC20;

    // Escrow details
    string public quizId;
    IERC20 public rewardToken;
    uint256 public rewardAmount;
    uint256 public expiryTime;
    bool public isActive = true;
    bool public rewardsDistributed = false;

    // Participant tracking
    mapping(address => bool) public isParticipant;
    mapping(address => uint256) public rewardAmounts;
    mapping(address => bool) public hasClaimed;
    address[] public participants;

    // Events
    event QuizEscrowCreated(string quizId, address creator, uint256 rewardAmount, uint256 expiryTime);
    event ParticipantAdded(address participant);
    event RewardsDistributed();
    event RewardClaimed(address participant, uint256 amount);
    event QuizCancelled();

    /**
     * @dev Constructor
     * @param _quizId Unique identifier for the quiz
     * @param _rewardToken Address of the ERC20 token used for rewards
     * @param _rewardAmount Total amount of tokens to distribute as rewards
     * @param _expiryTime Timestamp when the quiz expires
     * @param _creator Address of the quiz creator
     */
    constructor(
        string memory _quizId,
        address _rewardToken,
        uint256 _rewardAmount,
        uint256 _expiryTime,
        address _creator
    ) Ownable(_creator) {
        require(_expiryTime > block.timestamp, "QuizEscrow: Expiry time must be in the future");
        require(_rewardAmount > 0, "QuizEscrow: Reward amount must be greater than 0");
        require(_rewardToken != address(0), "QuizEscrow: Invalid token address");

        quizId = _quizId;
        rewardToken = IERC20(_rewardToken);
        rewardAmount = _rewardAmount;
        expiryTime = _expiryTime;

        emit QuizEscrowCreated(_quizId, _creator, _rewardAmount, _expiryTime);
    }

    /**
     * @dev Register a participant for the quiz
     * @param _participant Address of the participant
     */
    function registerParticipant(address _participant) external onlyOwner {
        require(isActive, "QuizEscrow: Quiz is no longer active");
        require(block.timestamp < expiryTime, "QuizEscrow: Quiz has expired");
        require(!isParticipant[_participant], "QuizEscrow: Already registered");

        isParticipant[_participant] = true;
        participants.push(_participant);

        emit ParticipantAdded(_participant);
    }

    /**
     * @dev Batch register multiple participants
     * @param _participants Array of participant addresses
     */
    function batchRegisterParticipants(address[] calldata _participants) external onlyOwner {
        require(isActive, "QuizEscrow: Quiz is no longer active");
        require(block.timestamp < expiryTime, "QuizEscrow: Quiz has expired");

        for (uint256 i = 0; i < _participants.length; i++) {
            address participant = _participants[i];
            if (!isParticipant[participant]) {
                isParticipant[participant] = true;
                participants.push(participant);
                emit ParticipantAdded(participant);
            }
        }
    }

    /**
     * @dev Distribute rewards to participants
     * @param _recipients Array of recipient addresses
     * @param _amounts Array of reward amounts
     */
    function distributeRewards(
        address[] calldata _recipients, 
        uint256[] calldata _amounts
    ) external onlyOwner {
        require(isActive, "QuizEscrow: Quiz is no longer active");
        require(block.timestamp >= expiryTime, "QuizEscrow: Quiz has not expired yet");
        require(!rewardsDistributed, "QuizEscrow: Rewards already distributed");
        require(_recipients.length == _amounts.length, "QuizEscrow: Arrays length mismatch");

        uint256 totalDistributed = 0;

        for (uint256 i = 0; i < _recipients.length; i++) {
            address recipient = _recipients[i];
            uint256 amount = _amounts[i];

            // Only distribute to registered participants
            if (isParticipant[recipient] && amount > 0) {
                rewardAmounts[recipient] = amount;
                totalDistributed += amount;
            }
        }

        // Ensure we're not distributing more than the reward amount
        require(totalDistributed <= rewardAmount, "QuizEscrow: Distribution exceeds reward amount");

        rewardsDistributed = true;
        isActive = false;

        emit RewardsDistributed();
    }

    /**
     * @dev Claim reward after distribution
     */
    function claimReward() external {
        require(!isActive, "QuizEscrow: Quiz is still active");
        require(rewardsDistributed, "QuizEscrow: Rewards not distributed yet");
        require(isParticipant[msg.sender], "QuizEscrow: Not a participant");
        require(!hasClaimed[msg.sender], "QuizEscrow: Already claimed reward");

        uint256 reward = rewardAmounts[msg.sender];
        require(reward > 0, "QuizEscrow: No reward to claim");

        hasClaimed[msg.sender] = true;
        rewardToken.safeTransfer(msg.sender, reward);

        emit RewardClaimed(msg.sender, reward);
    }

    /**
     * @dev Get all participants
     * @return Array of participant addresses
     */
    function getParticipants() external view returns (address[] memory) {
        return participants;
    }

    /**
     * @dev Check if a participant has a reward to claim
     * @param _participant Address of the participant
     * @return Amount of tokens claimable by the participant
     */
    function claimableReward(address _participant) external view returns (uint256) {
        if (!rewardsDistributed || !isParticipant[_participant] || hasClaimed[_participant]) {
            return 0;
        }
        return rewardAmounts[_participant];
    }

    /**
     * @dev Emergency function to cancel quiz and return tokens to owner
     * Can only be called by owner before rewards are distributed
     */
    function cancelQuiz() external onlyOwner {
        require(isActive, "QuizEscrow: Quiz is already closed");
        require(!rewardsDistributed, "QuizEscrow: Rewards already distributed");

        isActive = false;
        rewardToken.safeTransfer(owner(), rewardAmount);

        emit QuizCancelled();
    }
}

