contracts/src/QuizFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./QuizEscrow.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title QuizFactory
 * @dev Factory contract for creating and tracking quiz escrow contracts
 * The actual quiz logic is managed off-chain
 */
contract QuizFactory is Ownable {
    using SafeERC20 for IERC20;

    // Quiz escrow tracking
    struct QuizInfo {
        address escrowAddress;
        string quizId;
        address creator;
        address tokenAddress;
        uint256 rewardAmount;
        uint256 expiryTime;
        uint256 createdAt;
    }

    // Maps quiz ID to quiz info
    mapping(string => QuizInfo) public quizzes;

    // Maps creator address to their quiz IDs
    mapping(address => string[]) public creatorQuizzes;

    // All quiz IDs
    string[] public allQuizIds;

    // Events
    event QuizEscrowCreated(
        string quizId,
        address escrowAddress,
        address creator,
        address tokenAddress,
        uint256 rewardAmount,
        uint256 expiryTime
    );

    constructor() Ownable(msg.sender) {}

    /**
     * @dev Create a new quiz escrow to hold and distribute rewards
     * @param quizId Unique identifier for the quiz
     * @param tokenAddress Address of the ERC20 token used for rewards
     * @param rewardAmount Total amount of tokens to distribute as rewards
     * @param expiryTime Timestamp when the quiz expires (in seconds)
     * @return Address of the newly created quiz escrow contract
     */
    function createQuizEscrow(
        string memory quizId,
        address tokenAddress,
        uint256 rewardAmount,
        uint256 expiryTime
    ) external returns (address) {
        // Validate inputs
        require(bytes(quizId).length > 0, "QuizFactory: Quiz ID cannot be empty");
        require(quizzes[quizId].escrowAddress == address(0), "QuizFactory: Quiz ID already exists");
        require(tokenAddress != address(0), "QuizFactory: Invalid token address");
        require(rewardAmount > 0, "QuizFactory: Reward amount must be greater than 0");
        require(expiryTime > block.timestamp, "QuizFactory: Expiry time must be in the future");

        // Transfer tokens from creator to this contract
        IERC20 token = IERC20(tokenAddress);
        uint256 balanceBefore = token.balanceOf(address(this));
        // Since we have 'using SafeERC20 for IERC20' at the top, we can use it directly
        token.safeTransferFrom(msg.sender, address(this), rewardAmount);
        uint256 balanceAfter = token.balanceOf(address(this));

        // Verify the transfer was successful
        require(balanceAfter - balanceBefore >= rewardAmount, "QuizFactory: Token transfer failed");

        // Deploy new quiz escrow contract
        QuizEscrow newEscrow = new QuizEscrow(
            quizId,
            tokenAddress,
            rewardAmount,
            expiryTime,
            msg.sender
        );

        // Approve the escrow contract to spend the tokens
        // In OpenZeppelin v5.3.0, safeApprove is replaced with forceApprove
        token.forceApprove(address(newEscrow), rewardAmount);

        // Track the new quiz
        quizzes[quizId] = QuizInfo({
            escrowAddress: address(newEscrow),
            quizId: quizId,
            creator: msg.sender,
            tokenAddress: tokenAddress,
            rewardAmount: rewardAmount,
            expiryTime: expiryTime,
            createdAt: block.timestamp
        });

        // Add to tracking arrays
        creatorQuizzes[msg.sender].push(quizId);
        allQuizIds.push(quizId);

        emit QuizEscrowCreated(
            quizId,
            address(newEscrow),
            msg.sender,
            tokenAddress,
            rewardAmount,
            expiryTime
        );

        return address(newEscrow);
    }

    /**
     * @dev Get details of a quiz by ID
     * @param quizId The ID of the quiz to retrieve
     * @return Quiz information
     */
    function getQuizInfo(string memory quizId) external view returns (QuizInfo memory) {
        require(quizzes[quizId].escrowAddress != address(0), "QuizFactory: Quiz not found");
        return quizzes[quizId];
    }

    /**
     * @dev Get the escrow contract address for a quiz ID
     * @param quizId The ID of the quiz
     * @return Address of the escrow contract
     */
    function getEscrowAddress(string memory quizId) external view returns (address) {
        require(quizzes[quizId].escrowAddress != address(0), "QuizFactory: Quiz not found");
        return quizzes[quizId].escrowAddress;
    }

    /**
     * @dev Get all quiz IDs created by a specific address
     * @param creator Address of the quiz creator
     * @return Array of quiz IDs
     */
    function getQuizzesByCreator(address creator) external view returns (string[] memory) {
        return creatorQuizzes[creator];
    }

    /**
     * @dev Get the total number of quizzes created
     * @return Total number of quizzes
     */
    function getTotalQuizCount() external view returns (uint256) {
        return allQuizIds.length;
    }

    /**
     * @dev Get a paginated list of all quiz IDs
     * @param offset Starting index
     * @param limit Maximum number of items to return
     * @return Array of quiz IDs
     */
    function getQuizIds(uint256 offset, uint256 limit) external view returns (string[] memory) {
        uint256 total = allQuizIds.length;

        if (offset >= total) {
            return new string[](0);
        }

        uint256 count = (offset + limit > total) ? (total - offset) : limit;
        string[] memory result = new string[](count);

        for (uint256 i = 0; i < count; i++) {
            result[i] = allQuizIds[offset + i];
        }

        return result;
    }
}

